import { useContext } from 'react'
import { Nav, Navbar, NavDropdown, Container, NavItem, Button } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { ContextoLogin } from '../App'

export default function Menu(props) {
    const contextoLogin = useContext(ContextoLogin)

    return (
        <>
            <Navbar expand="lg" className="bg-body-tertiary mb-4">
                <Container>
                    <Navbar.Brand as={Link} to="/">Menu</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <NavDropdown title="Cadastros básicos" id="basic-nav-dropdown">
                                <NavDropdown.Item as={Link} to="/veiculo">Veículos</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item as={Link} to="/fornecedor">Fornecedor</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item as={Link} to="/concessionarias">Concessionárias</NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                    </Navbar.Collapse>

                    {/* Exibe o nome do usuário logado se estiver autenticado */}
                    {contextoLogin.logado && (
                        <Navbar.Text className="ms-auto">
                            Bem-vindo, {contextoLogin.usuario}
                        </Navbar.Text>
                    )}

                    {/* Se estiver logado, exibe o botão de logout */}
                    {contextoLogin.logado && (
                        <Button
                            variant="outline-danger"
                            onClick={() => {
                                contextoLogin.setDadosLogin({ usuario: "", logado: false })
                            }}
                        >
                            Logout
                        </Button>
                    )}
                </Container>
            </Navbar>
        </>
    )
}
